function binary2decimal(n) {
	var dec = 0;
	var l = n.length - 1;
	for (var i = 0; i < n.length - 1; i++) {
		if (n[i] === 1) {
			dec = dec + Math.pow(2, l);
		}
		l--;
	}
	return dec;
}

function decimal2binary(n) {
	var r = [];
	while (n != 0) {
		var rem = n % 2;
		r.push(rem);
		n = Math.floor(n / 2);
	}
	var i = 0;
	var j = r.length - 1;
	while (i < j) {
		var t = r[i];
		r[i] = r[j];
		r[j] = t;
		i++;
		j--;
	}
	return r;
}
