/**
 * This is a staircase of size : N = 4
 * !!!#
 * !!##
 * !###
 * ####
 * Its base and height are both equal to n. It is drawn 
 * using # symbols and !. The last line is not preceded 
 * by any !.
 *
 * Write a program that prints a staircase of size .
 * 
 * @param {*} height 
 * @param {*} base 
 */
function printStairs(height, base) {
	if (height > 0) {
		printStairs(height - 1, base + 1);
		var output = '';
		for (var i = 0; i <= base; i++) {
			output += '!';
		}
		for (var j = base; j < base + height; j++) {
			output += '#'
		}
		console.log(output);
	}
}
printStairs(6, 0);
